import React, { Component } from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

import Default from './Models/Default/Default'
import MasterCard from './Models/MasterCard/MasterCard'
import Visa from './Models/Visa/Visa'
import AmericanExpress from './Models/AmericanExpress/AmericanExpress'



class CardyModel extends Component {
    constructor(props) {
        super(props)
        this.state = {
            number: '',
            owner: '',
            expiration: '',
            cvv: '',
            type: '',
        }
    }

    componentDidUpdate = () => this.renderCardyModel()

    renderCardyModel = () => {
        switch (this.state.type) {
            case 'MasterCard':
                return <MasterCard />

            case 'Visa':
                return <Visa />

            case 'American Express':
                return <AmericanExpress />

            default:
                return <Default />
        }
    }

    render() {
        {this.renderCardyModel()}
    }
}

export default CardyModel
